
#!/bin/sh
###
# 定义内置变量
###
THIS_FILE_PATH=$(cd `dirname $0`; pwd)
# 私钥文件名字
PRIVITE_KEY_FILE_NAME=google-clound-ssr
PRIVITE_KEY_FILE_PATH=~/.ssh/
# 公钥文件名字
PUBLIC_KEY_FILE_NAME=${PRIVITE_KEY_FILE_NAME}.pub
# SSH服务所在主机地址
#SSH_SERVER_IP=34.97.0.37
SSH_SERVER_IP=192.168.2.2
# SSH服务所在主机账号
#SSH_SERVER_USER=yemiancheng
SSH_SERVER_USER=root
###
# 定义内置函数
###
function ouput_debug_msg() {
  local debug_msg=$1
  local debug_swith=$2
  if [[ "$debug_swith" =~ "false" ]]; then
    echo $debug_msg >/dev/null 2>&1
  elif [ -n "$debug_swith" ]; then
    echo $debug_msg
  elif [[ "$debug_swith" =~ "true" ]]; then
    echo $debug_msg
  fi
}
function path_resolve_for_relative() {
  local str1="${1}"
  local str2="${2}"
  local slpit_char1=/
  local slpit_char2=/
  if [[ -n ${3} ]]; then
    slpit_char1=${3}
  fi
  if [[ -n ${4} ]]; then
    slpit_char2=${4}
  fi

  # 路径-转为数组
  local arr1=(${str1//$slpit_char1/ })
  local arr2=(${str2//$slpit_char2/ })

  # 路径-解析拼接
  #2 遍历某一数组
  #2 删除元素取值
  #2 获取数组长度
  #2 获取数组下标
  #2 数组元素赋值
  for val2 in ${arr2[@]}; do
    length=${#arr1[@]}
    if [ $val2 = ".." ]; then
      index=$(($length - 1))
      if [ $index -le 0 ]; then index=0; fi
      unset arr1[$index]
      #echo ${arr1[*]}
      #echo  $index
    else
      index=$length
      arr1[$index]=$val2
      #echo ${arr1[*]}
    fi
  done
  # 路径-转为字符
  local str3=''
  for i in ${arr1[@]}; do
    str3=$str3/$i
  done
  if [ -z $str3 ]; then str3="/"; fi
  echo $str3
}
function path_resolve() {
  local str1="${1}"
  local str2="${2}"
  local slpit_char1=/
  local slpit_char2=/
  if [[ -n ${3} ]]; then
    slpit_char1=${3}
  fi
  if [[ -n ${4} ]]; then
    slpit_char2=${4}
  fi

  #FIX:when passed asboult path,dose not return the asboult path itself
  #str2="/d/"
  local str3=""
  str2=$(echo $str2 | sed "s#/\$##")
  ABSOLUTE_PATH_REG_PATTERN="^/"
  if [[ $str2 =~ $ABSOLUTE_PATH_REG_PATTERN ]]; then
    str3=$str2
  else
    str3=$(path_resolve_for_relative $str1 $str2 $slpit_char1 $slpit_char2)
  fi
  echo $str3
}
function get_help_msg() {
  local USAGE_MSG=$1
  local USAGE_MSG_FILE=$2
  if [ -z $USAGE_MSG ]; then
    if [[ -n $USAGE_MSG_FILE && -e $USAGE_MSG_FILE ]]; then
      USAGE_MSG=$(cat $USAGE_MSG_FILE)
    else
      USAGE_MSG="no help msg and file"
    fi
  fi
  echo "$USAGE_MSG"
}
# 引入相关文件
PROJECT_PATH=$(path_resolve $THIS_FILE_PATH "../")
HELP_DIR=$(path_resolve $THIS_FILE_PATH "../help")
SRC_DIR=$(path_resolve $THIS_FILE_PATH "../src")
TEST_DIR=$(path_resolve $THIS_FILE_PATH "../test")
DIST_DIR=$(path_resolve $THIS_FILE_PATH "../dist")
DOCS_DIR=$(path_resolve $THIS_FILE_PATH "../docs")
TOOL_DIR=$(path_resolve $THIS_FILE_PATH "../tool")
# 参数帮助信息
USAGE_MSG=
USAGE_MSG_PATH=$(path_resolve $THIS_FILE_PATH "../help")
USAGE_MSG_FILE=${USAGE_MSG_PATH}/connects-to-vm-with-ssh.txt
USAGE_MSG=$(get_help_msg "$USAGE_MSG" "$USAGE_MSG_FILE")
###
#参数规则内容
###
GETOPT_ARGS_SHORT_RULE="--options h,d,"
GETOPT_ARGS_LONG_RULE="--long help,debug,privite-key-file-name:,public-key-file-name:,privite-key-file-path:,ssh-server-ip:,ssh-server-user:"
###
#设置参数规则
###
GETOPT_ARGS=$(
  getopt $GETOPT_ARGS_SHORT_RULE \
  $GETOPT_ARGS_LONG_RULE -- "$@"
)
###
#解析参数规则
###
eval set -- "$GETOPT_ARGS"
# below generated by write-sources.sh
while [ -n "$1" ]
do
    case $1 in
    --privite-key-file-name)
    ARG_PRIVITE_KEY_FILE_NAME=$2
    shift 2
    ;;
    --public-key-file-name)
    ARG_PUBLIC_KEY_FILE_NAME=$2
    shift 2
    ;;
    --privite-key-file-path)
    ARG_PRIVITE_KEY_FILE_PATH=$2
    shift 2
    ;;
    --ssh-server-ip)
    ARG_SSH_SERVER_IP=$2
    shift 2
    ;;
    --ssh-server-user)
    ARG_SSH_SERVER_USER=$2
    shift 2
    ;;
    -h|--help)
    echo "$USAGE_MSG"
    exit 1
    ;;
    -d|--debug)
    IS_DEBUG_MODE=true
    shift 2
    ;;
    --)
    break
    ;;
    *)
    printf "$USAGE_MSG"
    ;;
    esac
done
###
#处理剩余参数
###
# optional
###
#更新内置变量
###
# below generated by write-sources.sh

if [ -n "$ARG_PRIVITE_KEY_FILE_NAME" ]
then
    PRIVITE_KEY_FILE_NAME=$ARG_PRIVITE_KEY_FILE_NAME
fi
if [ -n "$ARG_PUBLIC_KEY_FILE_NAME" ]
then
    PUBLIC_KEY_FILE_NAME=$ARG_PUBLIC_KEY_FILE_NAME
fi
if [ -n "$ARG_PRIVITE_KEY_FILE_PATH" ]
then
    PRIVITE_KEY_FILE_PATH=$ARG_PRIVITE_KEY_FILE_PATH
fi
if [ -n "$ARG_SSH_SERVER_IP" ]
then
    SSH_SERVER_IP=$ARG_SSH_SERVER_IP
fi
if [ -n "$ARG_SSH_SERVER_USER" ]
then
    SSH_SERVER_USER=$ARG_SSH_SERVER_USER
fi
###
#脚本主要代码
###
#2 查看密钥
ls $PRIVITE_KEY_FILE_PATH | grep $PRIVITE_KEY_FILE_NAME
cat ~/.ssh/known_hosts
#2 生成密钥（根据ssh-keygen参数和gcp规范）
$WORKING_FILE=${PRIVITE_KEY_FILE_PATH}${PRIVITE_KEY_FILE_NAME}
if [ -e $WORKING_FILE ]
then
    echo "yes" > /dev/null 2>&1
else
    #echo "no"
   ssh-keygen -t rsa -f $WORKING_FILE -C $SSH_SERVER_USER
fi
#2 查看公钥
cat $WORKING_FILE
#2 上传公钥
ssh-copy-id -i ${PRIVITE_KEY_FILE_PATH}${PUBLIC_KEY_FILE_NAME} $SSH_SERVER_USER@$SSH_SERVER_IP
#scp -p ${PRIVITE_KEY_FILE_PATH}$PUBLIC_KEY_FILE_NAME $SSH_SERVER_USER@$SSH_SERVER_IP:~/.ssh/authorized_keys
#2 添加私钥--添加私钥到~/.ssh/known_hosts文件当中
#cat ~/.ssh/known_hosts | grep "$SSH_SERVER_IP"
eval $(ssh-agent -s)
ssh-add ${PRIVITE_KEY_FILE_PATH}${PRIVITE_KEY_FILE_NAME}
#2 免密登录
ssh -i ${PRIVITE_KEY_FILE_PATH}${PRIVITE_KEY_FILE_NAME} $SSH_SERVER_USER@$SSH_SERVER_IP
